import UIKit

protocol DetailBusinessLogic {
    func doLoadMovieData(request: Detail.Movie.Request)
}

protocol DetailDataStore {
    var movieViewModel: MovieViewModel { get set }
}

class DetailInteractor: DetailBusinessLogic, DetailDataStore {
    var movieViewModel: MovieViewModel
    
    init(movieViewModel: MovieViewModel) {
        self.movieViewModel = movieViewModel
    }
    
    var presenter: DetailPresentationLogic?
    var worker: DetailWorker?
    
    // MARK: Do something
    
    func doLoadMovieData(request: Detail.Movie.Request) {
        worker = DetailWorker()
        let response = Detail.Movie.Response()
        self.presenter?.presentDetailData(response: response)
        worker?.getImage(posterURL: movieViewModel.getPosterURL(), completionHandler: { image in
            self.presenter?.updateDetailImage(response: response, uiImage: image)
        })
    }
}
