import UIKit

protocol DetailPresentationLogic {
    func presentDetailData(response: Detail.Movie.Response)
    func updateDetailImage(response: Detail.Movie.Response, uiImage: UIImage)
}

class DetailPresenter: DetailPresentationLogic {
    weak var viewController: DetailDisplayLogic?
    
    // MARK: Do something
    
    func presentDetailData(response: Detail.Movie.Response) {
        let viewModel = Detail.Movie.ViewModel()
        viewController?.displayMovieData(viewModel: viewModel)
    }
    
    func updateDetailImage(response: Detail.Movie.Response, uiImage: UIImage) {
        let viewModel = Detail.Movie.ViewModel()
        viewController?.displayImageMovieData(viewModel: viewModel, uiImage: uiImage)
    }
}
