import UIKit

class DetailWorker {
    func getImage(posterURL: URL?, completionHandler: @escaping (UIImage) -> Void) {
        if let posterURL = posterURL {
            ImageDownloader.getImage(withURL: posterURL) { image in
                if let image = image {
                    completionHandler(image)
                }
            }
        }
    }
}
